USE [master]
GO
IF DB_ID('BeanWater') IS NULL
	CREATE DATABASE [BeanWater]
GO
USE [BeanWater]
GO

DROP TABLE IF EXISTS [drinkTools]; 
DROP TABLE IF EXISTS [favorites];
DROP TABLE IF EXISTS [tools];
DROP TABLE IF EXISTS [drinks];
DROP TABLE IF EXISTS [drinkTypes];
DROP TABLE IF EXISTS [users];

CREATE TABLE [users] (
  [Id] int PRIMARY KEY identity NOT NULL,
  [Email] nvarchar(255),
  [displayName] nvarchar(255),
  [uId] nvarchar(255) NOT NULL UNIQUE,
)
GO

CREATE TABLE [drinks] (
  [Id] int PRIMARY KEY identity NOT NULL,
  [Name] nvarchar(255),
  [drinkTypesId] int,
  [drinksImg] nvarchar(255),
  [Recipe] varchar(8000)
)
GO

CREATE TABLE [drinkTypes] (
  [Id] int PRIMARY KEY identity NOT NULL,
  [Type] nvarchar(255)
)
GO

CREATE TABLE [tools] (
  [Id] int PRIMARY KEY identity NOT NULL,
  [Name] nvarchar(255),
  [Link] varchar(1080),
)
GO

CREATE TABLE [drinkTools] (
  [Id] int PRIMARY KEY identity NOT NULL,
  [drinkId] int,
  [toolId] integer
)
GO

CREATE TABLE [favorites] (
  [Id] int PRIMARY KEY identity NOT NULL,
  [drinkId] int,
  [uId] nvarchar(255)
)
GO


























ALTER TABLE [favorites] ADD FOREIGN KEY ([uId]) REFERENCES [users] ([uId])
GO

ALTER TABLE [favorites] ADD FOREIGN KEY ([drinkId]) REFERENCES [drinks] ([Id])
GO

ALTER TABLE [drinks] ADD FOREIGN KEY ([drinkTypesId]) REFERENCES [drinkTypes] ([Id])
GO

ALTER TABLE [drinkTools] ADD FOREIGN KEY ([toolId]) REFERENCES [tools] ([Id])
GO

ALTER TABLE [drinkTools] ADD FOREIGN KEY ([drinkId]) REFERENCES [drinks] ([Id])
GO
